import axios from 'axios';
import { LoginData, SignupData, AuthResponse } from '../types';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests if available
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle token expiration
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const authAPI = {
  login: (data: LoginData): Promise<AuthResponse> =>
    api.post('/auth/login', data).then(res => res.data),
  
  signup: (data: SignupData) =>
    api.post('/auth/signup', data).then(res => res.data),
};

export const userAPI = {
  getUsers: () => api.get('/users').then(res => res.data),
  getProfile: () => api.get('/users/profile').then(res => res.data),
  updateUser: (id: string, data: any) => 
    api.put(`/users/${id}`, data).then(res => res.data),
  deleteUser: (id: string) => 
    api.delete(`/users/${id}`).then(res => res.data),
};

export const projectAPI = {
  getProjects: () => api.get('/projects').then(res => res.data),
  getProject: (id: string) => api.get(`/projects/${id}`).then(res => res.data),
  createProject: (data: any) => api.post('/projects', data).then(res => res.data),
  updateProject: (id: string, data: any) => 
    api.put(`/projects/${id}`, data).then(res => res.data),
  deleteProject: (id: string) => 
    api.delete(`/projects/${id}`).then(res => res.data),
  addTeamMember: (projectId: string, userId: string) =>
    api.post(`/projects/${projectId}/team`, { user_id: userId }).then(res => res.data),
  removeTeamMember: (projectId: string, userId: string) =>
    api.delete(`/projects/${projectId}/team/${userId}`).then(res => res.data),
};

export const taskAPI = {
  getTasks: (params?: any) => {
    const queryString = params ? `?${new URLSearchParams(params).toString()}` : '';
    return api.get(`/tasks${queryString}`).then(res => res.data);
  },
  getTask: (id: string) => api.get(`/tasks/${id}`).then(res => res.data),
  createTask: (data: any) => api.post('/tasks', data).then(res => res.data),
  updateTask: (id: string, data: any) => 
    api.put(`/tasks/${id}`, data).then(res => res.data),
  updateTaskStatus: (id: string, status: string) =>
    api.patch(`/tasks/${id}/status`, { status }).then(res => res.data),
  addComment: (id: string, comment: string) =>
    api.post(`/tasks/${id}/comments`, { comment }).then(res => res.data),
  deleteTask: (id: string) => 
    api.delete(`/tasks/${id}`).then(res => res.data),
};

export const reportAPI = {
  getTasksByStatus: () => api.get('/reports/tasks-by-status').then(res => res.data),
  getOverdueTasks: () => api.get('/reports/overdue-tasks').then(res => res.data),
  getDashboard: () => api.get('/reports/dashboard').then(res => res.data),
  getUserWorkload: () => api.get('/reports/user-workload').then(res => res.data),
};

export default api;
